settings.custom_css:
.container-gloss {  display: grid;
  grid-template-columns: 16% 16% 34% 34%;
  grid-template-rows: auto;
  gap: 0px 0px;
  grid-auto-flow: row;
  grid-template-areas:
    ". . . .";
}

.container-gloss.gloss-heading {
    font-weight: 600;
}

.container-gloss div {
    border-top: 1px solid #ccc;
    padding: 5px;
}
